import{_ as a,c as n,o as s,ak as e}from"./chunks/framework.CmzJUe0_.js";const t="/assets/blog_20241223171313.Dze9u5cF.png",u=JSON.parse('{"title":"pnpm笔记","description":"","frontmatter":{"title":"pnpm笔记","date":"2024-12-23T00:00:00.000Z","sidebar":"auto","categories":["前端"],"tags":["NodeJS","pnpm"]},"headers":[],"relativePath":"blogs/NodeJS/pnpm笔记.md","filePath":"blogs/NodeJS/pnpm笔记.md"}'),l={name:"blogs/NodeJS/pnpm笔记.md"};function i(m,p,o,r,d,c){return s(),n("div",null,p[0]||(p[0]=[e('<p>[toc]</p><h1 id="pnpm笔记" tabindex="-1">pnpm笔记 <a class="header-anchor" href="#pnpm笔记" aria-label="Permalink to &quot;pnpm笔记&quot;">​</a></h1><p><a href="https://www.pnpm.cn/" target="_blank" rel="noreferrer">pnpm中文官网 https://www.pnpm.cn/</a></p><p><img src="'+t+`" alt="blog_20241223171313.png"></p><p>pnpm 本质上就是一个包管理器，这一点跟 npm/yarn 没有区别，但它的优点在于:</p><ul><li>包安装速度极快；</li><li>磁盘空间利用非常高效。</li></ul><h2 id="pnpm的安装" tabindex="-1">pnpm的安装 <a class="header-anchor" href="#pnpm的安装" aria-label="Permalink to &quot;pnpm的安装&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># 先通过npm，全局安装 pnpm</span></span>
<span class="line"><span>npm i -g pnpm</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 验证安装</span></span>
<span class="line"><span>pnpm -v</span></span></code></pre></div><h2 id="pnpm的使用" tabindex="-1">pnpm的使用 <a class="header-anchor" href="#pnpm的使用" aria-label="Permalink to &quot;pnpm的使用&quot;">​</a></h2><p>pnpm的使用与npm的使用大致相同。</p><h3 id="pnpm-add" tabindex="-1">pnpm add <a class="header-anchor" href="#pnpm-add" aria-label="Permalink to &quot;pnpm add&quot;">​</a></h3><p>pnpm add 命令用于安装依赖包。默认会从 npm 仓库中安装最新的依赖包。</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>## 安装vue软件包</span></span>
<span class="line"><span>pnpm add vue</span></span></code></pre></div><h3 id="pnpm-install-别名-pnpm-i" tabindex="-1">pnpm install (别名 pnpm i) <a class="header-anchor" href="#pnpm-install-别名-pnpm-i" aria-label="Permalink to &quot;pnpm install (别名 pnpm i)&quot;">​</a></h3><p>pnpm install 用于安装项目中包含的所有依赖包。</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>pnpm install</span></span></code></pre></div><h3 id="pnpm-remove-别名-pnpm-rm-pnpm-uninstall" tabindex="-1">pnpm remove (别名 pnpm rm，pnpm uninstall) <a class="header-anchor" href="#pnpm-remove-别名-pnpm-rm-pnpm-uninstall" aria-label="Permalink to &quot;pnpm remove (别名 pnpm rm，pnpm uninstall)&quot;">​</a></h3><p>pnpm remove 从 node_modules 和项目 package.json 中删除软件包。</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># 删除项目中的vue软件包</span></span>
<span class="line"><span>pnpm remove vue</span></span>
<span class="line"><span></span></span>
<span class="line"><span>pnpm rm vue</span></span>
<span class="line"><span></span></span>
<span class="line"><span>pnpm uninstall vue</span></span></code></pre></div><h3 id="pnpm-update-别名-pnpm-upgrade" tabindex="-1">pnpm update （别名 pnpm upgrade） <a class="header-anchor" href="#pnpm-update-别名-pnpm-upgrade" aria-label="Permalink to &quot;pnpm update （别名 pnpm upgrade）&quot;">​</a></h3><p>pnpm update 根据指定的范围更新软件包的最新版本。若在不带参数的情况下使用时，将更新所有软件包的版本。</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># 更新vue软件包的版本</span></span>
<span class="line"><span>pnpm update vue</span></span></code></pre></div><h2 id="pnpm-和-npm-yarn-的区别" tabindex="-1">pnpm 和 npm/yarn 的区别 <a class="header-anchor" href="#pnpm-和-npm-yarn-的区别" aria-label="Permalink to &quot;pnpm 和 npm/yarn 的区别&quot;">​</a></h2><p><a href="https://www.cnblogs.com/cangqinglang/p/14448329.html" target="_blank" rel="noreferrer">参考文章 https://www.cnblogs.com/cangqinglang/p/14448329.html</a></p>`,24)]))}const g=a(l,[["render",i]]);export{u as __pageData,g as default};
