import{_ as r}from"./chunks/20230531233151.C82DYOOL.js";import{_ as i}from"./chunks/unraid_20230531234841.CPDTy0YK.js";import{_ as t,c as o,o as a,ak as l}from"./chunks/framework.CmzJUe0_.js";const f=JSON.parse('{"title":"Zerotier内网穿透","description":"","frontmatter":{"title":"Zerotier内网穿透","date":"2023-05-31T00:00:00.000Z","sidebar":"auto","tags":["内网穿透"]},"headers":[],"relativePath":"blogs/内网穿透/Zerotier内网穿透.md","filePath":"blogs/内网穿透/Zerotier内网穿透.md"}'),s={name:"blogs/内网穿透/Zerotier内网穿透.md"};function n(p,e,d,_,c,Z){return a(),o("div",null,e[0]||(e[0]=[l('<p>[toc]</p><h1 id="zerotier内网穿透" tabindex="-1">Zerotier内网穿透 <a class="header-anchor" href="#zerotier内网穿透" aria-label="Permalink to &quot;Zerotier内网穿透&quot;">​</a></h1><p>使用Zerotier，可以无需公网IP就能进行内网穿透。</p><p>注意: 只有当控制端和被控制端都安装了Zerotier.两端之间才能在不需要公网ip的情况下,进行端对端访问.</p><h2 id="注册zerotier和设置" tabindex="-1">注册Zerotier和设置 <a class="header-anchor" href="#注册zerotier和设置" aria-label="Permalink to &quot;注册Zerotier和设置&quot;">​</a></h2><p><a href="https://www.zerotier.com/" target="_blank" rel="noreferrer">Zerotier官网</a></p><ol><li>注册Zerotier账户</li><li>创建网络，点击“Create A Network”</li><li>网络创建之后，就可以看到网络的NETWORK ID</li></ol><h2 id="自己电脑安装zerotier软件" tabindex="-1">自己电脑安装Zerotier软件 <a class="header-anchor" href="#自己电脑安装zerotier软件" aria-label="Permalink to &quot;自己电脑安装Zerotier软件&quot;">​</a></h2><ol><li>在Zerotier官网上下载安装包,完成软件的安装.</li><li>运行Zerotier软件.</li><li>选中zerotier图标,鼠标右击zerotier，选择“Join Network”。</li><li>输入你创建网络的NETWORK ID即可.</li><li>然后登录Zerotier官网.选中你创建的网络.</li><li>你就可以在Members设置中看到你的电脑出现在这个网络中. <img src="'+r+'" alt="20230531233151.png"></li><li>勾选方框，刷新页面,这样就把你电脑加入到你创建的网络中。</li><li>Managed IPs表示的是你的设备在这个网络中的ip,如果你要访问其他设备,就需要在浏览器中输入其他设备的Managed ip。</li></ol><h2 id="nas端安装zerotier" tabindex="-1">nas端安装Zerotier <a class="header-anchor" href="#nas端安装zerotier" aria-label="Permalink to &quot;nas端安装Zerotier&quot;">​</a></h2><p>以unraid系统为例，在unraid系统中安装Zerotier。</p><ol><li>直接在应用市场中搜索Zerotier</li><li>安装Zerotier docker容器</li><li>在docker配置页面，填写在Zerotier网站中你注册的NETWORK ID即可。该docker容器会自动下载安装。</li><li>登录Zerotier官网，把nas端设备添加到你的网络中即可。</li><li>然后再浏览器中访问nas端设备分配到的ip地址，即可远程访问nas</li></ol><p>注意设置自动启动 <img src="'+i+'" alt="unraid_20230531234841.png"></p>',13)]))}const b=t(s,[["render",n]]);export{f as __pageData,b as default};
