import{_ as i,c as o,o as e,ak as a}from"./chunks/framework.CmzJUe0_.js";const p="/assets/softRouter_20230716211219.COzzM5-2.png",s="/assets/softRouter_20230716211609.DfPGBdc_.png",r="/assets/softRouter_20230716205838.C26Q_s80.png",l="/assets/softRouter_20230716210246.CEAN805s.png",n="/assets/softRouter_20230716210606.S3PgJZnj.png",m=JSON.parse('{"title":"IPV6笔记","description":"","frontmatter":{"title":"IPV6笔记","date":"2023-07-16T00:00:00.000Z","sidebar":"auto","categories":["软路由"],"tags":["软路由"]},"headers":[],"relativePath":"blogs/软路由/IPV6笔记.md","filePath":"blogs/软路由/IPV6笔记.md"}'),_={name:"blogs/软路由/IPV6笔记.md"};function c(v,t,d,f,u,g){return e(),o("div",null,t[0]||(t[0]=[a('<p>[toc]</p><h1 id="ipv6笔记" tabindex="-1">IPV6笔记 <a class="header-anchor" href="#ipv6笔记" aria-label="Permalink to &quot;IPV6笔记&quot;">​</a></h1><h2 id="如何查询当前设备的ipv6是多少" tabindex="-1">如何查询当前设备的ipv6是多少？ <a class="header-anchor" href="#如何查询当前设备的ipv6是多少" aria-label="Permalink to &quot;如何查询当前设备的ipv6是多少？&quot;">​</a></h2><blockquote><p>windows系统：cmd-&gt;ipconfig</p></blockquote><p><img src="'+p+'" alt="softRouter_20230716211219.png"></p><blockquote><p>linux系统：终端-》ifconfig</p></blockquote><p><img src="'+s+'" alt="softRouter_20230716211609.png"></p><ul><li>一般f开头的ipv6地址，是内网地址。24开头的地址是ipv6外网地址。</li><li>ipv6采用了临时ipv6地址和长期ipv6地址,什么区别呢,临时ipv6地址,是你访问外网的时候使用的,外部其他人是无法连进来的,并且有效时间比较短,可以有效防止追踪,而长期ipv6地址,则默认不用来访问外部,一般用来部署服务,让外部用户连进来进行访问,有效期比较长,</li></ul><h2 id="如何检测是否有ipv6" tabindex="-1">如何检测是否有ipv6 <a class="header-anchor" href="#如何检测是否有ipv6" aria-label="Permalink to &quot;如何检测是否有ipv6&quot;">​</a></h2><ol><li><p>访问这个网站 <a href="http://test-ipv6.com/index.html.zh_CN" target="_blank" rel="noreferrer">ipv6测试</a></p></li><li><p>若结果如图所示，则表示你当前设备已经有了ipv6的地址了。 <img src="'+r+'" alt="softRouter_20230716205838.png"></p></li></ol><h2 id="如何测试ipv6的地址是否能从外网访问" tabindex="-1">如何测试ipv6的地址是否能从外网访问 <a class="header-anchor" href="#如何测试ipv6的地址是否能从外网访问" aria-label="Permalink to &quot;如何测试ipv6的地址是否能从外网访问&quot;">​</a></h2><ol><li>访问这个网站 <a href="https://ipw.cn/ipv6ping/" target="_blank" rel="noreferrer">IPv6 Ping 测试</a></li><li>输入想要访问的ipv6地址。点击测试。</li><li>如果如图所示，则表示你的ipv6网址还不能从外网访问。</li></ol><p><img src="'+l+'" alt="softRouter_20230716210246.png"></p><p>访问失败的原因：如果你当前的设备是windows系统。你需要在windows系统上关闭ipv6防火墙。关闭防火墙之后，才有可能访问成功。如果是其他系统，那么也可能需要关闭防火墙。</p><ol start="4"><li>如果如图所示。那么表示你的ipv6网址能从外网访问。</li></ol><p><img src="'+n+'" alt="softRouter_20230716210606.png"></p>',16)]))}const P=i(_,[["render",c]]);export{m as __pageData,P as default};
