import{_ as i,c as e,o,ak as a}from"./chunks/framework.CmzJUe0_.js";const u=JSON.parse('{"title":"重装开发环境","description":"","frontmatter":{"title":"重装开发环境","date":"2022-07-03T00:00:00.000Z","sidebar":"auto","categories":["随笔"]},"headers":[],"relativePath":"blogs/随笔/重装开发环境.md","filePath":"blogs/随笔/重装开发环境.md"}'),t={name:"blogs/随笔/重装开发环境.md"};function s(n,l,r,_,d,c){return o(),e("div",null,l[0]||(l[0]=[a('<h2 id="重装开发环境" tabindex="-1">重装开发环境 <a class="header-anchor" href="#重装开发环境" aria-label="Permalink to &quot;重装开发环境&quot;">​</a></h2><blockquote><p>前端开发</p></blockquote><ol><li>vscode</li><li>安装nvm来管理多版本的node.js <s>node.js (nodejs自带npm包管理工具。yarn包管理工具需要另外安装)</s></li><li>vue-cli (vue的脚手架。安装它也安装了vue。通过npm来安装vue-cli)</li><li>nginx</li></ol><blockquote><p>后端开发</p></blockquote><ol><li>git</li><li>jdk(配置JAVA_HOME环境变量，在path环境变量中添加JAVA_HOME)</li><li>mysql (密码设置为123456，有时候需要安装依赖包：Microsoft Visual C++ 2019 Redistributable Package (x64))</li><li>navicat（安装破解版）</li><li>idea (安装破解版，注意配置maven)</li><li>redis (安装windows版本的redis，密码设置为123456)</li><li>RedisDesktopManager (redis数据库管理工具)</li><li>maven(配置MAVEN_HOME环境变量，在path环境变量中添加MAVEN_HOME。注意修改maven配置文件中的本地仓库地址，远程仓库地址，编译环境信息)</li><li>nacos（微服务注册中心，安装可选。若启动nacos闪退，检查是否配置了JAVA_HOME环境变量）</li><li>xftp,xshell（可选）</li></ol><p>PS:</p><ul><li>maven的1.x版本需要配置MAVEN_HOME环境变量。2.x,3.x版本的maven需要配置M2_HOME环境变量</li></ul><blockquote><p>其他软件</p></blockquote><ol><li>360压缩</li><li>edge浏览器或者chrome浏览器(注意安装vue插件)</li><li>foxmail（邮件管理工具,注意导入邮件）</li><li>微信（注意导入聊天记录）</li><li>QQ</li><li>wps</li><li>安装windows版本的kindle (阅读pdf电子书)</li><li>滴答清单（待办事项管理）</li><li>potplayer(好用的视频播放器)</li><li>向日葵（可选）</li></ol>',9)]))}const m=i(t,[["render",s]]);export{u as __pageData,m as default};
