import{_ as i,c as r,o as n,ak as l}from"./chunks/framework.CmzJUe0_.js";const c=JSON.parse('{"title":"SpringCloud面试题总结","description":"","frontmatter":{"title":"SpringCloud面试题总结","date":"2020-11-24T00:00:00.000Z","sidebar":"auto","categories":["面试"],"tags":["SpringCloud","SpringCloudAlibaba"]},"headers":[],"relativePath":"blogs/面试/SpringCloud面试题总结.md","filePath":"blogs/面试/SpringCloud面试题总结.md"}'),a={name:"blogs/面试/SpringCloud面试题总结.md"};function p(e,o,t,d,g,u){return n(),r("div",null,o[0]||(o[0]=[l('<p>[toc]</p><h1 id="springcloud面试题总结" tabindex="-1">SpringCloud面试题总结 <a class="header-anchor" href="#springcloud面试题总结" aria-label="Permalink to &quot;SpringCloud面试题总结&quot;">​</a></h1><h2 id="什么是-spring-cloud" tabindex="-1">什么是 Spring Cloud？ <a class="header-anchor" href="#什么是-spring-cloud" aria-label="Permalink to &quot;什么是 Spring Cloud？&quot;">​</a></h2><p>Spring cloud 是基于 Spring Boot 的分布式微服务架构解决方案。</p><p>Spring cloud 可以将基于Spring Boot开发的单体微服务整合并管理起来,是一系列框架的集合。</p><p>Spring cloud 提供如注册中心、配置中心、API网关、流量监控、断路器、链路追踪等功能。</p><h2 id="springboot和springcloud的区别" tabindex="-1">SpringBoot和SpringCloud的区别？ <a class="header-anchor" href="#springboot和springcloud的区别" aria-label="Permalink to &quot;SpringBoot和SpringCloud的区别？&quot;">​</a></h2><p>SpringBoot专注于开发单个微服务。</p><p>SpringCloud是分布式微服务系统解决方案。它将SpringBoot开发的单体微服务整合起来，提供如注册中心、配置中心、API网关、流量监控、断路器、链路追踪等功能。</p><h2 id="什么是springcloudconfig" tabindex="-1">什么是SpringCloudConfig? <a class="header-anchor" href="#什么是springcloudconfig" aria-label="Permalink to &quot;什么是SpringCloudConfig?&quot;">​</a></h2><p>在分布式系统中，由于服务数量巨多，为了方便服务配置文件统一管理，实时更新，所以需要分布式配置中心组件。在Spring Cloud中，有分布式配置中心组件spring cloud config ，它支持配置服务放在配置服务的内存中（即本地），也支持放在远程Git仓库中。</p><p>在spring cloud config 组件中，分两个角色，一是config server，二是config client。使用：（1）添加pom依赖（2）配置文件添加相关配置（3）启动类添加注解@EnableConfigServer</p><h2 id="springcloud涉及到的核心组件及其作用" tabindex="-1">SpringCloud涉及到的核心组件及其作用？ <a class="header-anchor" href="#springcloud涉及到的核心组件及其作用" aria-label="Permalink to &quot;SpringCloud涉及到的核心组件及其作用？&quot;">​</a></h2><p>Spring cloud 是基于 Spring Boot 的微服务架构解决方案，是一套框架。专门用于解决微服务架构下的各种问题。</p><p>为了解决微服务架构下出现的各种问题，SpringCloud都提供了对于的解决方案。</p><p>SpringCloud的核心组件如下。</p><ul><li>服务的注册与发现：eureka,Nacos</li><li>服务之间的互相调用：Ribbon,openFeign</li><li>服务熔断降级组件：Hystrix,Sentinel</li><li>API网关：Zuul,SpringCloudGateway</li><li>配置中心：SpringCloudConfig</li><li>链路追踪：skywalking</li><li>分布式事务：Seata</li></ul>',17)]))}const S=i(a,[["render",p]]);export{c as __pageData,S as default};
